import { Nav, Navbar, Row, Col, Form, Button ,Container } from 'react-bootstrap';
import { NavLink } from 'react-router-dom';
import { connect } from 'react-redux';
import { useState } from 'react';
import { api_login } from './api';

function LoginForm() {
  const [email, setName] = useState("");
  const [pass, setPass] = useState("");

  function on_submit(ev) {
    ev.preventDefault();
    api_login(email, pass);
  }

  return (
    <Form onSubmit={on_submit} inline>
      <div className="mr-1 h4 font-weight-bold text-light ml-5">Email:</div>
      <Form.Control name="email"
                    type="text"
                    onChange={(ev) => setName(ev.target.value)}
                    value={email}
                    className="ml-1 mr-1"/>
      <div className="mr-1 h4 font-weight-bold text-light">Password:</div>
      <Form.Control name="password"
                    type="password"
                    onChange={(ev) => setPass(ev.target.value)}
                    value={pass}
                    className="ml-1"/>
      <Button variant="primary" type="submit">
        <div className="h4 font-weight-bold text-light btn btn-sm">Login</div>
      </Button>
    </Form>
  );
}

function SessionInfo({session}) {
  return (
    <div className="h4 mt-2 font-weight-bold">Logged in as {session.email}</div>
  );
}

function LOI({session}) {
  if (session) {
    return <SessionInfo session={session} />;
  }
  else {
    return <LoginForm />;
  }
}

const LoginOrInfo = connect(
  ({session}) => ({session}))(LOI);

function Link({to, children}) {
  return (
    <Nav.Item>
      <NavLink to={to} exact className="nav-link"
               activeClassName="active">
        {children}
      </NavLink>
    </Nav.Item>
  );
}

export default function AppNav() {
  return (
      <div>
      <Row>
        <Navbar fluid className="container bg-info">
          <div className="h1 font-weight-bold text-light mr-5">HWZone</div>
          <LoginOrInfo />
        </Navbar>
      </Row>
      <Row>
        <Col className="mt-4">
          <Nav variant="tabs" defaultActiveKey="/">
            <Link to="/">Home</Link>
            <Link to="/users">Users</Link>
          </Nav>
        </Col>
      </Row>
      </div>
  );
}
