import { useState, useEffect } from 'react';
import { Container } from "react-bootstrap";
import "./App.scss";
import Users from "./Users";

async function fetchUsers(xs) {
  let text = await fetch("http://events-spa.teresewang.com:4000/api/v1/users", {});
  let resp = await text.json();
  return resp.data;
}

function App() {
  const [users, setUsers] = useState([]);
  useEffect(() => {
    if (users.length == 0) {
      fetchUsers().then((xs) => setUsers(xs));
    }
  }, [users.length]);

  return(
    <Container>
      <Users users={users} />
    </Container>
  );
}

export default App;
