import store from './store';

//referenced from lecture code SPA Structure from Nat Tuck CS4550 Northeastern University
async function api_get(path) {
  let text = await fetch(
    "http://localhost:4000/api/v1" + path, {});
  let resp = await text.json();
  return resp.data;
}

async function api_post(path, data) {
  let opts = {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(data),
  };
  let text = await fetch(
    "http://localhost:4000/api/v1" + path, opts);
  return await text.json();
}

export function fetch_users() {
    api_get("/users").then((data) => store.dispatch({
        type: 'users/set',
        data: data,
    }));
}

export function fetch_user(id) {
  api_get("/users/" + id).then((data) => store.dispatch({
      type: 'user_form/set',
      data: data,
  }));
}

export async function create_user(user) {
  let data = new FormData();
  data.append("user[name]", user.name);
  data.append("user[email]", user.email);
  data.append("user[password]", user.password);
  let resp = await fetch("http://localhost:4000/api/v1/users", {
    method: "POST",
    body: data,
  })
return await resp.json();
}

export async function update_user(user) {
  let data = new FormData();
  data.append("user_id", user.id);
  data.append("user[name]", user.name);
  data.append("user[email]", user.email);
  data.append("user[password]", user.password);
  let resp = await fetch("http://localhost:4000/api/v1/users/" + user.id, {
    method: "PATCH",
    body: data,
  })
return await resp.json();
}

export function fetch_events() {
  api_get("/events").then((data) => {
    store.dispatch({
    type: 'events/set',
    data: data,
  })});
}

export function fetch_event(id) {
  api_get("/events/"+id).then((data) => store.dispatch({
    type: 'event_form/set',
    data: data,
  }));
}

export async function create_event(event) {
  let data = new FormData();
  data.append("event[user_id]", event.user_id);
  data.append("event[date]", event.date);
  data.append("event[description]", event.description);
  data.append("event[name]", event.name);
  let resp = await fetch("http://localhost:4000/api/v1/events", {
    method: "POST",
    body: data,
  })
return await resp.json();
}


export async function update_event(event) {
  let data = new FormData();
  data.append("event[user_id]", event.user_id);
  data.append("event[date]", event.date);
  data.append("event[description]", event.description);
  data.append("event[name]", event.name);
  let resp = await fetch("http://localhost:4000/api/v1/events/" + event.id, {
    method: "PATCH",
    body: data,
  })
return await resp.json();
}

export async function delete_event(id) {
  let data = new FormData();
  data.append("id", id);
  fetch("http://kittenlover-backend.teresewang.com/api/v1/foods/" + id, {
    method: 'DELETE',
    body: data,
  }).then((resp) => {
    if(!resp.ok){
      let action = {
        type: 'error/set',
        data: 'Unable to delete event.',
      };
      store.dispatch(action);
    }else {
      fetch_events();
    }
  });
}

export function api_login(email, password) {
  api_post("/session", {email, password}).then((data) => {
    if (data.session) {
      let action = {
        type: 'session/set',
        data: data.session,
      }
      store.dispatch(action);
    }
    else if (data.error) {
     let action = {
        type: 'error/set',
        data: data.error,
      }
      store.dispatch(action);
    }
  });
}

