import { Button, Nav, NavRow, Row, Col, Card, Form } from 'react-bootstrap';
import { connect } from 'react-redux';
import { Link, useHistory, useLocation, NavLink } from 'react-router-dom';
import { useState } from 'react';
import { fetch_events, fetch_event, fetch_user} from '../api';
import store from '../store';

function EventView({session, event_form, user_form}) {
  let location = useLocation()
  let history = useHistory()
  let event_id = location.pathname.split("/events/view/")[1]

  if(typeof(event_form.id) == "undefined" || event_form.id != event_id) {
    fetch_events()
    fetch_event(event_id)
  }

  if(typeof(user_form.id) == "undefined" || user_form.id != event_form.user_id){
    fetch_events()
    fetch_user(event_form.user_id)
  }

  function AddButtons() {
    if(session && session.user_id == user_form.id) {
      let link = "/events/edit/" + event_form.id
      return(
        <div>
        <Link to={link} className="btn mt-2 btn-lg btn-info text-light mr-2 font-weight-bold">Edit</Link>
        <Link to="/" className="btn btn-lg mt-2 btn-info text-light  font-weight-bold">Back</Link>
        </div>
      )
    }
    else {
      return(
        <Link to="/" className="btn btn-lg mt-2 btn-danger text-light  font-weight-bold">Back</Link>
      )
    }
  }

    if (session) {
      return(
        <div>
          <AddButtons/>
          <div class="row">
            <div class="col">
              <h1 className="display-3">{event_form.name}</h1>
              <h1 className="h4 text-secondary">Posted By: {user_form.name}</h1>
              <h1 className="h4 text-secondary">Event Date: {event_form.date}</h1>
              <p className="h3 mt-2 text-dark">{event_form.description}</p>
            </div>
          </div>
        </div>
      )
    }
    else {
      return(
        <div>
          <AddButtons/>
          <h1 className="h4 text-secondary">Posted by: {user_form.name}</h1>
          <div class="row">
            <div class="col">
              <h1 className="display-3">{event_form.brand}</h1>
              <h1 className="h3">Price</h1>
              <p className="h5 mt-2 text-danger">{event_form.price}$ per Lbs</p>
              <h1 className="h3">Type</h1>
              <p className="h5 mt-2 text-secondary">{event_form.type}</p>
              <h1 className="h3">Description</h1>
              <p className="h5 mt-2 text-secondary">{event_form.body}</p>
            </div>
          </div>
        </div>
      )
    }
  }

function state2props({session, event_form, user_form}) {
  return {session, event_form, user_form};
}

export default connect(state2props)(EventView);
